mapper.update:
update region
set code = #{code}
where regionNo=#{regionNo}

update region
set code = #{code}, parentNo=#{parentNo}
where regionNo=#{regionNo}

mapper.select:
select p.* from post p
	left outer join post_region pr on p.postID=pr.postID and pr.regionName=#{value}
where pr.postID is null
