mapper.select:
SELECT * FROM ( SELECT P.postID, P.userID, type, message, content, fromLatitude, fromLongitude, fromAddress, latitude, longitude, toAddress, reward, P.createdDate, P.userID as 'user.userID', U.profileImageURL as 'user.profileImageURL', U.userName as 'user.userName', U.sex as 'user.sex', YEAR(NOW())-YEAR(U.birthday)+1 as 'user.age' , PR.cnt as "replyCount", sexInfo, numOfUsers, departureDate, departureTime, departureDateTime
,TRUNCATE(( 6371 * acos ( cos ( radians( #{fromLatitude} ) ) * cos( radians( fromLatitude ) ) * cos( radians( fromLongitude ) - radians( #{fromLongitude} ) ) + sin ( radians( #{fromLatitude} ) ) * sin( radians( fromLatitude ) ) ) ) , 1) AS fromDistance
,TRUNCATE(( 6371 * acos ( cos ( radians( #{toLatitude} ) ) * cos( radians( latitude ) ) * cos( radians( longitude ) - radians( #{toLongitude} ) ) + sin ( radians( #{toLatitude} ) ) * sin( radians( latitude ) ) ) ) , 1) AS toDistance
,status, vehicle, fareOption, repetitiveYN, RC.readCount as readCount FROM post P left outer join (select count(replyID) as 'cnt', postID from post_reply where deletedDate is null group by postID ) PR on P.postID=PR.postID left outer join user U on P.userID=U.userID left outer join ( select postID, count(postID) as readCount from post_read_history group by postID ) RC on P.postID=RC.postID WHERE fromLatitude is not null and fromLongitude is not null and type='taxi' and deletedDate is null ) A WHERE 1=1
and postID=#{postID}
AND A.fromDistance <= #{fromDistance}
AND A.toDistance <= #{toDistance}
AND A.status = #{status}
order by toDistance, fromDistance, A.status desc, createdDate desc
order by fromDistance, A.status desc, createdDate desc
order by toDistance, A.status desc, createdDate desc
order by A.status desc, createdDate desc
LIMIT #{pageStart} , #{pageSize};

SELECT count(postID) FROM ( SELECT P.postID, P.userID, type, message, content, fromLatitude, fromLongitude, fromAddress, latitude, longitude, toAddress, reward, P.createdDate, P.userID as 'user.userID', U.profileImageURL as 'user.profileImageURL', U.userName as 'user.userName', U.sex as 'user.sex', YEAR(NOW())-YEAR(U.birthday)+1 as 'user.age' , PR.cnt as "replyCount", sexInfo, numOfUsers, departureDate, departureTime
,TRUNCATE(( 6371 * acos ( cos ( radians( #{fromLatitude} ) ) * cos( radians( fromLatitude ) ) * cos( radians( fromLongitude ) - radians( #{fromLongitude} ) ) + sin ( radians( #{fromLatitude} ) ) * sin( radians( fromLatitude ) ) ) ) , 1) AS fromDistance
,TRUNCATE(( 6371 * acos ( cos ( radians( #{toLatitude} ) ) * cos( radians( latitude ) ) * cos( radians( longitude ) - radians( #{toLongitude} ) ) + sin ( radians( #{toLatitude} ) ) * sin( radians( latitude ) ) ) ) , 1) AS toDistance
,status FROM post P left outer join (select count(replyID) as 'cnt', postID from post_reply where deletedDate is null group by postID ) PR on P.postID=PR.postID left outer join user U on P.userID=U.userID WHERE fromLatitude is not null and fromLongitude is not null and type='taxi' and deletedDate is null ) A WHERE 1=1
AND A.fromDistance <= #{fromDistance}
AND A.toDistance <= #{toDistance}
AND A.status = #{status}


		SELECT count(userID) FROM (
			SELECT *, TRUNCATE((
    			6371 * acos ( cos ( radians( #{fromLatitude} ) )
	      		* cos( radians( latitude ) )
  	 	   		* cos( radians( longitude ) - radians( #{fromLongitude} ) )
   		   		+ sin ( radians( #{fromLatitude} ) )
   		   		* sin( radians( latitude ) )
   		 		)
  				) , 1)  AS distance
			FROM (
				select UL.*, U.userName
				from user_location UL
					LEFT OUTER JOIN user U ON UL.userID=U.userID
				where locationName='현재위치'
			) A
			WHERE latitude is not null
			and longitude is not null
			ORDER BY distance
		) A
		WHERE A.distance <= #{distance}
		and A.userID <> #{userID}
		and A.userID <> 'admin'
		and A.userName is not null;


		SELECT * FROM (
			SELECT *, TRUNCATE((
    			6371 * acos ( cos ( radians( #{fromLatitude} ) )
	      		* cos( radians( latitude ) )
  	 	   		* cos( radians( longitude ) - radians( #{fromLongitude} ) )
   		   		+ sin ( radians( #{fromLatitude} ) )
   		   		* sin( radians( latitude ) )
   		 		)
  				) , 1)  AS distance
			FROM (
				select UL.*, U.userName, U.regID 
				from user_location UL
					LEFT OUTER JOIN user U ON UL.userID=U.userID
				where locationName='현재위치'
			) A
			WHERE latitude is not null
			and longitude is not null
			ORDER BY distance
		) A
		WHERE A.distance <= #{distance}
		and A.userID <> #{userID}
		and A.userID <> 'admin'
		and A.userName is not null;

select *
from user_location
where userID=#{userID}

